import { useState } from "react";

const Example = () => {
  const arr1 = [1, 2, 3];
  console.log(...arr1);
  const [state, setState] = useState(0);
  const countUp = () => {
    setState((prev) => ++prev);
  };
  return (
    <>
      <h3>{state}</h3>
      <button onClick={countUp}>+</button>
    </>
  );
};

export default Example;
